main :: IO ()
main = hspec $ do
    it "Sle Frame" $ do
        let result =
                parseASN1 parseRafTransferDataInvocation singleTransferFrame
        result `shouldSatisfy` isRight
        T.putStrLn $ "Result: " <> fromString (show result)
    it "Sle Frame Or Notification" $ do
        let result =
                parseASN1 parseFrameOrNotification singleFrameOrNotification
        result `shouldSatisfy` isRight
        T.putStrLn $ "Result: " <> fromString (show result)
    it "Sle Transfer Buffer" $ do
        let result = parseASN1 (slePduParser RtnAllFrames) transData
        result `shouldSatisfy` isRight
      -- result `shouldBe` Right (ServiceInstanceAttribute SPACK "VST-PASS0001")

        T.putStrLn $ "Result: " <> fromString (show result)
